<?phpfunction fizzBuzz(): void{    for ($i = 1; $i <= 100; $i++) {        switch ($i) {            case  $i % 3 === 0 && $i % 5 === 0:                echo 'FizzBuzz ';                break;            case $i % 5 === 0:                echo 'Buzz ';                break;            case $i % 3 === 0:                echo 'fizz ';                break;            default:                echo "{$i} ";        }    }}function sortBySurname(array $names){        $fullname = array_map(fn($item) => explode(' ', $item), $names);        usort($fullname, function ($a, $b){           if ($a === $b){               return 0;           }           return $a < $b ? -1: 1;        });    $result =  array_map(fn($item) => "{$item[0]} {$item[1]}", $fullname);    return $result;}//fizzBuzz();var_dump(sortBySurname(["Linus Torvalds", "Bill Gates","Barbara Liskov"]));