<?phpnamespace App\Tests;use PHPUnit\Framework\TestCase;use App\UrlClass;class UrlClassTest extends TestCase{    public function testUrl1()    {        $url = new UrlClass('http://yandex.ru?key=value&key2=value2');        $this->assertEquals('http', $url->getScheme());        $this->assertEquals('yandex.ru', $url->getHost());        $params = [            'key' => 'value',            'key2' => 'value2'        ];        $this->assertEquals($params, $url->getQueryParams());        $this->assertEquals('value', $url->getQueryParam('key'));        $this->assertEquals('value2', $url->getQueryParam('key2', 'lala'));        $this->assertEquals('ehu', $url->getQueryParam('new', 'ehu'));    }    public function testUrl2()    {        $url = new UrlClass('https://google.com?a=b&c=d&lala=value');        $this->assertEquals('https', $url->getScheme());        $this->assertEquals('google.com', $url->getHost());        $params = [            'a' => 'b',            'c' => 'd',            'lala' => 'value'        ];        $this->assertEquals($params, $url->getQueryParams());        $this->assertNull($url->getQueryParam('key'));    }}